from cot_retriever.upload import split

def test_split():
  assert split('a, b, c') == ['a', 'b', 'c']
  assert split('a,b,c') == ['a', 'b', 'c']
  assert split('"a", b') == ['a', 'b']
  assert split('"a, b", c') == ['a, b', 'c']
  assert split('"a, b ", c') == ['a, b ', 'c']
  assert split('"a, b " , c') == ['a, b ', 'c']
  assert split(',"a, b " , c') == ['', 'a, b ', 'c']
  assert split(',,, ') == ['', '', '', ''] # line does not end with ,
  assert split(',"a, b " , c,') == ['', 'a, b ', 'c', '']
  assert split(',"a, b " , c, ') == ['', 'a, b ', 'c', '']
  assert split(' ,"a," , " b " , c, ') == ['', 'a,', ' b ', 'c', '']

